<div id="popUp"
    class="hidden pointer-events-none top-0 place-content-center fixed z-10 h-screen w-screen flex items-center">
    <div
        class="border-slate-100 pointer-events-auto bg-white z-30  w-fit p-4 rounded-lg border flex flex-col space-y-2">
        <h1 id="title" class="text-center font-bold"></h1>
        <div id="popUp-data" class="flex space-x-2">

        </div>
    </div>
</div>
<div id="cover-popUp"
    class="pointer-events-auto fixed top-0 z-0 h-screen w-screen bg-black bg-opacity-50 flex justify-center hidden">
</div>
<script>
    const buttonDbd = document.getElementById('data-dbd')
    const buttonHiv = document.getElementById('data-hiv')
    const buttonIspa = document.getElementById('data-ispa')
    const popUp = document.getElementById('popUp')
    const title = document.getElementById('title')
    const popUpData = document.getElementById('popUp-data')
    const containerData = document.createElement('div')
    const coverPopUp = document.getElementById('cover-popUp')

    function renderDetailParam(gambar, title) {
        const data = `
            <div class="border border-slate-200 h-fit flex flex-col space-y-2 drop-shadow-md rounded bg-white p-2">
                <h1 class="text-center">${title}</h1>
                <img src=${gambar} alt='gambar'
                    width='200'>
                <a class="text-center p-2 bg-[#D95639] rounded text-white"
                    href=${gambar}
                    download='data ${title} terbaru'>Download Gambar</a>
            </div>
        `
        containerData.innerHTML += data
        containerData.classList.add('flex')
        containerData.classList.add('space-x-2')
        popUpData.append(containerData)
    }

    buttonDbd.addEventListener('click', function() {
        title.innerHTML = 'Data DBD'
        containerData.innerHTML = ''
        popUp.classList.remove('hidden')
        coverPopUp.classList.remove('hidden')
        @if ($penyakitDbd && $penyakitDbd->gambar)
            renderDetailParam("{{ asset('storage/dataPenyakitDbd/' . $penyakitDbd->gambar) }}", 'Penyakit DBD');
        @else
            console.error("Penyakit DBD data not available");
        @endif

        @if ($kepadatanDbd && $kepadatanDbd->gambar)
            renderDetailParam("{{ asset('storage/kepadatanPendudukDbd/' . $kepadatanDbd->gambar) }}",
                'Kepadatan Penduduk');
        @else
            console.error("Kepadatan Penduduk DBD data not available");
        @endif

        @if ($faktorDbd && $faktorDbd->gambar)
            renderDetailParam("{{ asset('storage/faktorLingkunganDbd/' . $faktorDbd->gambar) }}",
                'Faktor Lingkungan');
        @else
            console.error("Faktor Lingkungan DBD data not available");
        @endif
    })

    buttonHiv.addEventListener('click', function() {
        title.innerHTML = 'Data HIV'
        containerData.innerHTML = ''
        popUp.classList.remove('hidden')
        coverPopUp.classList.remove('hidden')
        @if ($kasusHiv && $kasusHiv->gambar)
            renderDetailParam("{{ asset('storage/kasusHiv/' . $kasusHiv->gambar) }}", 'Penyakit HIV');
        @else
            console.error("Kasus HIV data not available");
        @endif

        @if ($hotspot && $hotspot->gambar)
